mapper.select:
SELECT 

FROM user_accountchange a

WHERE a.id = #{id}

SELECT IFNULL(sum(change_money),0) FROM user_accountchange
money_type = 1
AND user_name = #{userName}
AND change_type = #{changeType}
AND DATE_FORMAT(create_date, '%Y-%m-%d') = DATE_FORMAT(#{date}, '%Y-%m-%d')

SELECT
IFNULL(sum(change_money),0)
FROM user_accountchange

	change_money < 0

		AND DATE_FORMAT(create_date, '%Y-%m') = DATE_FORMAT(#{date}, '%Y-%m')


		AND user_name = #{userName}


		AND money_type = #{moneyType}


		AND change_type = #{changeType}

SELECT 

FROM user_accountchange a 




		AND a.user_name = #{userName}


		AND a.change_type = #{changeType}


		AND a.money_type = #{moneyType}


		AND a.money_type <= #{moenyTypeEnd}


		AND DATE_FORMAT(a.create_date, '%Y-%m-%d') = DATE_FORMAT(#{createDate}, '%Y-%m-%d')


		AND DATE_FORMAT(a.create_date, '%Y-%m') = DATE_FORMAT(#{createDateMonth}, '%Y-%m')


		AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= DATE_FORMAT(#{createDateBegin}, '%Y-%m-%d')


		AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= DATE_FORMAT(#{createDateEnd}, '%Y-%m-%d')


		AND a.change_money > #{changeMoneyBegin}


		AND a.change_money < #{changeMoneyEnd}


		AND a.ischeck = #{ischeck}


		AND a.isMonCheck = #{isMonCheck}



		AND a.status = #{status}


		AND u.parent_list like  concat('%/',#{userUserinfo.parentListLike},'/%')


	AND a.change_type IN

			#{item}






		ORDER BY ${page.orderBy}


		ORDER BY ${orderBy}


		ORDER BY a.id DESC



	limit 0,#{topLimit}

SELECT a.id AS "id", a.user_name AS "userName", sum(a.change_money) AS "changeMoney", a.status AS "status", a.change_type AS "changeType", a.money_type AS "moneyType", a.commt AS "commt", a.create_date AS "createDate", u.true_name AS "userUserinfo.trueName" FROM user_accountchange a LEFT JOIN user_userinfo u ON u.user_name = a.user_name
AND a.change_type = #{changeType}
AND a.money_type = #{moneyType}
AND a.user_name = #{userName}
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') >= DATE_FORMAT(#{createDateBegin}, '%Y-%m-%d')
AND DATE_FORMAT(a.create_date, '%Y-%m-%d') <= DATE_FORMAT(#{createDateEnd}, '%Y-%m-%d')
GROUP BY a.user_name,DATE_FORMAT(a.create_date,'%Y-%m-%d')
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC
