mapper.select:
SELECT 

FROM user_photo a

WHERE a.id = #{id}

SELECT 


FROM user_photo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.keyword = #{keyword}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY ${orderBy}


		ORDER BY a.update_date DESC
